import React, { useState } from "react";
import moonStars from "../assets/moon-stars.svg";
import sun from "../assets/sun.svg";

function Toggle() {

    const toggleMode = () => {
        console.log("clicked");

        const moonIcon = document.getElementById("moonIcon");
        const sunIcon = document.getElementById("sunIcon");

        moonIcon.classList.add("hide");
        sunIcon.classList.remove("hide");
    }

  return (
    <div className="toggle">
      <div className="toggle__div">
          <img className="toggle__moon-icon" src={moonStars} id="moonIcon" alt="moon and stars icon" />

          <div className="toggle__slider" onClick={toggleMode}></div>

          <img className="toggle__sun-icon" id="sunIcon" src={sun} alt="sun icon" />
      </div>
    </div>
  )
}

export default Toggle
