import React, { useState } from 'react'
import Nav from '../components/Nav'
import $ from 'jquery'

// styles
import '../sass/style.scss';

function Arcade() {
    const question = document.querySelector('#question');
    const eightBall = document.querySelector('#eightBall');

    function prediction(){
        console.log('testing');

        const randomNumber = Math.floor(Math.random() * 8);

        let answer = '';

        $('#answer').empty();

        switch(randomNumber){
            case 0:
                answer = 'It is certain'
                break;
            case 1:
                answer = 'It is likely so'
                break;
            case 2:
                answer = 'Vision hazy, try again'
                break;
            case 3:
                answer = 'Cannot predict now'
                break;
            case 4:
                answer = 'Do not count on it'
                break;
            case 5:
                answer = 'Computer says no'
                break;
            case 6:
                answer = 'Outlook not so good'
                break;
            case 7:
                answer = 'Signs point to yes'
                break;
        }

        $('#answer').append(
            `
            <p>${answer}</p>
            `
        );
    }

    eightBall.addEventListener('click', prediction);

    function launch8BallModal() {
        document.getElementById("modalBackground").style.display = "block";
        document.getElementById("8BallModal").style.display = "block";
    } 

    function launchMadLibsModal() {
        document.getElementById("modalBackground").style.display = "block";
        document.getElementById("madLibsModal").style.display = "block";
    } 

    function clodeModal() {
        console.log("close")
        document.getElementById("modalBackground").style.display = "none";
        document.getElementById("madLibsModal").style.display = "none";
        document.getElementById("8BallModal").style.display = "none";
    }

    return (
        <>
            <div className="modal" id="8BallModal">
                <div className="modal__text-div">
                    <h1 className="modal__heading">Magic 8 ball</h1>
                    <p className="modal__light-p">Nov 2021</p>
                    <p className="modal__p">The second game I ever coded</p>
                </div>

                <div className="eightball-game">
                    <div className="eightball-game__answer" id="answer"></div>

                    <p className="eightball-game__8ball" id="eightBall">🎱</p>
                </div>
            </div>

            <div className="modal" id="madLibsModal"></div>

            <div className="modal-background" id="modalBackground" onClick={clodeModal}>
            </div>

            <Nav />
            <div className="right-page">
                <div className="arcade-info-section">
                    <h1 className="arcade-info-section__heading">‍👾 Arcade</h1>

                    <p className="arcade-info-section__middle-p">Come get your geek on!</p>

                    <p className="arcade-info-section__p">When I first started learning Javascript I felt overwhelmed trying to understand its logic and structure. It was a big step up from HTML & CSS! 
                    <br />
                    <br />
                    I started making little Javascript games in my spare time to practice using parameters and exploring various methods.
                    <br />
                    <br />
                    In the process, not only did I discover my technical capabilities, but I also discovered how creative coding could be, and my fear soon turned into excitement.</p>
                </div>

                <div className="arcade">
                    <div className="arcade__card arcade__card-b-g" onClick={launch8BallModal}>
                        <p className="arcade__big-emoji">🎱</p>

                        <div className="arcade__text-div hide">
                            <p className="arcade__p">Magic 8 ball</p>
                            <button className="arcade__play-btn">Play</button>
                        </div>
                    </div>

                    <div className="arcade__card arcade__card-p-b" onClick={launchMadLibsModal}>
                        <p className="arcade__big-emoji">🐸</p>

                        <div className="arcade__text-div hide">
                            <p className="arcade__p">Emoji mad libs</p>
                            <button className="arcade__play-btn">Play</button>
                        </div>
                    </div>
                </div>

            </div>
        </>
    )
}

export default Arcade
