import React from 'react';

// Media
import earthenwaresLaptop from "../assets/earthenwares-laptop.png";
import earthenwaresLaptopDark from "../assets/earthenwares-laptop-dark.png";
import peruseLaptop from "../assets/peruse-laptop.png";
import stayNzLaptop from "../assets/stay-nz-laptop.png";
import stayNz from "../assets/stay-nz-frame.svg";
import bigStar from '../assets/big-star.svg';
import smallStar from '../assets/small-star.svg';
import bigStarYellow from '../assets/big-star-yellow.svg';
import smallStarYellow from '../assets/small-star-yellow.svg';
import blueRadialGlow from '../assets/blue-radial-glow.svg';

function SchoolProjects() {

  const showProjectInfo = (id) => {
    document.getElementById(id[0]).classList.add("hide");
    document.getElementById(id[1]).classList.remove("hide");
  }

  const hideProjectInfo = (id) => {
    document.getElementById(id[0]).classList.remove("hide");
    document.getElementById(id[1]).classList.add("hide");
  }

  return (
    <div className="school-projects">
      <div className="school-projects__text-div">
        <h2 className="school-projects__heading">School projects</h2>
        <p className="school-projects__p">These projects are a benchmark for how far Iâ€™ve come in my personal learning. The syllabus didn't include React or Redux, so these projects work with vanilla JavaScript & jQuery.</p>

        {/* <div className="school-projects__bottom-left-star-div">
          <img className="school-projects__big-star-bl school-projects__twinkle" src={bigStar} alt="star icon" />
          <img className="school-projects__small-star-one-bl school-projects__twinkle" src={smallStar} alt="star icon" />
          <img className="school-projects__small-star-two-bl school-projects__twinkle" src={smallStar} alt="star icon" />
        </div> */}
      </div>

      <div className="school-projects__projects-container">

        <div className="school-projects__project-div">
          <img className="school-projects__img" id="earthenwaresLaptop" onMouseOver={() => showProjectInfo("earthenwaresLaptop", "earthenwaresLaptopDark")} onMouseOffr={() => hideProjectInfo("earthenwaresLaptop", "earthenwaresLaptopDark")} src={earthenwaresLaptop} alt="iPhone mockup of Stay NZ project" />
          <img className="school-projects__img hide" id="earthenwaresLaptopDark" src={earthenwaresLaptopDark} alt="iPhone mockup of Stay NZ project" />

          <div className="school-projects__hover-info hide">
              <p className="school-projects__p"></p>
              <button className="school-projects__btn">Check it out</button>
          </div>
        </div>

        <div className="school-projects__project-div">
          <img className="school-projects__img" src={stayNzLaptop} alt="iPhone mockup of Stay NZ project" />

          <div className="school-projects__hover-info hide">
              <p className="school-projects__p"></p>
              <button className="school-projects__btn">Check it out</button>
          </div>
        </div>

        <div className="school-projects__project-div">
          <img className="school-projects__img" src={peruseLaptop} alt="iPhone mockup of Stay NZ project" />

          <div className="school-projects__hover-info hide">
              <p className="school-projects__p"></p>
              <button className="school-projects__btn">Check it out</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default SchoolProjects;
