            <div className="madlibs-game" id="modalGame">
                <div className="madlibs-game__emoji-div">
                    <p className={emojiOneClasses}>🧚🏼‍♂️👑</p>
                    <p className={emojiTwoClasses}>🔮✨</p>
                    <p className={emojiThreeClasses} id="button" onClick={nextPage}>✨⛰️</p>
