import React, { useState } from 'react'
import Nav from '../components/Nav'
import $ from 'jquery'

// styles
import '../sass/style.scss';
import Eightball from '../components/Eightball';
import EmojiMadlibs from '../components/EmojiMadlibs';

function Arcade() {

    function launch8BallModal() {
        // document.getElementById("modalBackground").style.display = "block";
        // document.getElementById("8BallModal").style.display = "block";
        // document.getElementById("modalText").style.display = "block";
        // document.getElementById("modalGame").style.display = "block";

        document.getElementById("modalBackground").classList.remove = "hide";
        document.getElementById("8BallModal").classList.remove = "hide";
        // document.getElementById("modalText").classList.remove = "hide";
        // document.getElementById("modalGame").classList.remove = "hide";
    } 

    function launchMadLibsModal() {
        // document.getElementById("modalBackground").style.display = "block";
        // document.getElementById("madLibsModal").style.display = "block";
        document.getElementById("modalBackground").classList.remove = "hide";
        document.getElementById("madLibsModal").classList.remove = "hide";
    } 

    function clodeModal() {
        // console.log("close")
        // document.getElementById("modalBackground").style.display = "none";
        // document.getElementById("madLibsModal").style.display = "none";
        // document.getElementById("8BallModal").style.display = "none";
        document.getElementById("modalBackground").classList.add = "hide";
        document.getElementById("madLibsModal").classList.add = "hide";
        document.getElementById("8BallModal").classList.add = "hide";
    }

    return (
        <>
            <Eightball />

            <EmojiMadlibs />

            <div className="modal-background hide" id="modalBackground" onClick={clodeModal}>
            </div>

            <Nav />
            <div className="right-page">
                <div className="arcade-info-section">
                    <h1 className="arcade-info-section__heading">‚Äçüëæ Arcade</h1>

                    <p className="arcade-info-section__middle-p">Come get your geek on!</p>

                    <p className="arcade-info-section__p">When I first started learning Javascript I felt overwhelmed trying to understand its logic and structure. It was a big step up from HTML & CSS! 
                    <br />
                    <br />
                    I started making little Javascript games in my spare time to practice using parameters and exploring various methods.
                    <br />
                    <br />
                    In the process, not only did I develop my technical capabilities, but I also discovered how creative coding could be, and my fear soon turned into excitement.</p>
                </div>

                <div className="arcade">
                    <div className="arcade__card arcade__card-b-g" onClick={launch8BallModal}>
                        <p className="arcade__big-emoji">üé±</p>

                        <div className="arcade__text-div hide">
                            <p className="arcade__p">Magic 8 ball</p>
                            <button className="arcade__play-btn">Play</button>
                        </div>
                    </div>

                    <div className="arcade__card arcade__card-p-b" onClick={launchMadLibsModal}>
                        <p className="arcade__big-emoji">üê∏</p>

                        <div className="arcade__text-div hide">
                            <p className="arcade__p">Emoji mad libs</p>
                            <button className="arcade__play-btn">Play</button>
                        </div>
                    </div>
                </div>

            </div>
        </>
    )
}

export default Arcade
